package com.lwb.music.base;import android.content.ContentResolver;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.lwb.music.interfaces.MusicController;public abstract class BaseFragment extends Fragment implements View.OnClickListener {    protected View rootView;    protected ContentResolver resolver;    protected MusicController musicController;    public void setMusicController(MusicController musicController) {        this.musicController = musicController;    }    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        if (resolver == null) {            resolver = getActivity().getContentResolver();        }        if (rootView == null) {            rootView = inflater.inflate(getLayout(), container, false);            initView(rootView);        }        initData();        return rootView;    }    protected abstract int getLayout();    protected abstract void initView(View view);    protected void initData() {    }    public void startFragment(Fragment fragment) {        BaseActivity activity = (BaseActivity) getActivity();        activity.startFragment(fragment);    }    @Override    public void onClick(View v) {    }}