package com.lwb.music.bean;public class LrcFile {    private String fileName;    private String path;    public String getFileName() {        return fileName;    }    public void setFileName(String fileName) {        this.fileName = fileName;    }    public String getPath() {        return path;    }    public void setPath(String path) {        this.path = path;    }    @Override    public String toString() {        return "LrcFile{" +                "fileName='" + fileName + '\'' +                ", path='" + path + '\'' +                '}';    }}